-- 创建一个新用户，指定用户名，密码
CREATE USER new_user IDENTIFIED BY your_password; 
-- 授予用户连接选项，不然用户无法连接数据库
GRANT CONNECT TO new_user; 
-- 授予用户对某张表的查询权限
GRANT SELECT ON schema_owner.ERP_SHEDULE_SHEET TO new_user; 
GRANT SELECT ON schema_owner.TRACK_MATERIAL TO new_user;

dba.BeginTrans("dba.udl"); // 连接数据库创建事务
sql = s("insert into TB_PRODUCT_HIS select * from TB_PRODUCT where DISCHARGE_TIME<sysdate-90");  // 事务语句
cmd = dba.CreateCommand(); 
cmd->CommandText = (_bstr_t)sql;
cmd->Execute(NULL, 0, adCmdText); // 执行查询
dba.CommitTrans("dba.udl"); 、// 事务提交
// dba.RollbackTrans; 事务回滚
